const CampsiteCard = ({ imageUrl, campsiteName, description, price }) => {
  return (
    <div className="campsite-card">
      <img src={imageUrl} alt={campsiteName} className="campsite-image" />
      <div className="campsite-details">
        <h3 className="campsite-name">{campsiteName}</h3>
        <p className="campsite-description">{description}</p>
        <div className="campsite-footer">
          <button className="add-button">
            <Link to="/campsite-detail">Book Now</Link>
          </button>
          <p className="campsite-price">{price}</p>
        </div>
      </div>
    </div>
  );
};


const CampsiteList = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState(campsites);

  const fuse = new Fuse(campsites, {
    keys: ["campsiteName", "description"],
    includeScore: true,
    threshold: 0.4,
  });

  const handleSearch = (event) => {
    if (!event.target.value) {
      setSearchResults(campsites);
      return;
    }
    setSearchTerm(event.target.value);
    const results = fuse.search(event.target.value);
    setSearchResults(results.map((result) => result.item));
  };

  return (
    <div>
      <div id="searchbox">
        <input
          type="search"
          name="search"
          placeholder="Search Here.."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <div className="campsite-card-container">
        {searchResults.map((campsite) => (
          <CampsiteCard
            key={campsite.id}
            imageUrl={campsite.imageUrl}
            campsiteName={campsite.campsiteName}
            description={campsite.description}
            price={campsite.price}
          />
        ))}
      </div>
    </div>
  );
};

export default CampsiteList;
